mapper.select:
SELECT ai.id,ai.owner,ai.title,ai.cover,ai.content_text,ti.name,ai.type_id, DATE_FORMAT(ai.update_time,'%Y-%m-%d') as update_time, ai.view_count,u.username,ai.status FROM Article_info ai,type_info ti,user u WHERE ai.type_id=ti.id and u.id=ai.owner and ai.owner=#{owner} and ai.status=#{status} and ai.type_id=#{typeId}
and ai.update_time between #{startDate} and #{endDate}
and ai.title like '%${keyWord}%' ORDER BY ai.update_time DESC

mapper.update:
UPDATE article_info
type_id=#{type_id}, title=#{title}, content=#{content}, content_text=#{content_text}, cover=#{cover}, view_count=#{view_count}, update_time=#{update_time}, status=#{status},
WHERE id = #{id}

